package week1_day2;

public class Operatorshandson {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Arithmetic operator
System.out.println(8+5);//13
System.out.println(8-5);//3
System.out.println(5*5);//25
System.out.println(10%5);//0
System.out.println(20/5);//4
	
	//Assignment operator
	
	int a=5;
		System.out.println(a+=10);//15
		System.out.println(a-=2);//13
	
	// Comparison operator
	
	
		System.out.println(5==5);//True
		System.out.println(8!=5);//True
		System.out.println(6<5);//False
		System.out.println(5<13);//True
		
	
	//Logical Operator
	
		System.out.println((5>9) && (5==5)); //And operator//False
		System.out.println((5>9) || (5==5)); //OR Operator//True
	
	

}
}
